import "./portfolio.scss"
import PortfolioList from "../portfolioList/PortfolioList"
import { useState } from "react"
import {
    featuredPortfolio,
    webappPortfolio,
    traditionalArt,
    digitalartPortfolio,
    photomanipulationPortfolio
} from "../../data"



export default function Portfolio() {
    // when selected matches an id, that id will be the one selected
    const [selected, setSelected]= useState("featured")
    const list = [
        {
            id:"featured",
            title:"Featured"
        },
        {
            id:"webapp",
            title:"Web App"
        },
        {
            id:"traditionalart",
            title:"Tradition Art"
        },
        {
            id:"digitalart",
            title:"Digital Art"
        },
        {
            id:"photomanipulation",
            title:"Photo Manipulation"
        }
    ]
    return (
        <div className="portfolio" id="portfolio">
            <h1>Portfolio</h1>
            <ul>
                {list.map(item=>(
                    <PortfolioList 
                    title={item.title}
                    active={selected===item.id} 
                    setSelected={setSelected}
                    id={item.id}
                    />
                ))}
            </ul>
            <div className="container">
                <div className="item">
                    <img src="assets/site.png" alt="" />
                    <h3>Portfolio Site</h3>
                </div>
                <div className="item">
                    <img src="assets/site.png" alt="" />
                    <h3>Portfolio Site</h3>
                </div>
                <div className="item">
                    <img src="assets/site.png" alt="" />
                    <h3>Portfolio Site</h3>
                </div>
                <div className="item">
                    <img src="assets/site.png" alt="" />
                    <h3>Portfolio Site</h3>
                </div>
                <div className="item">
                    <img src="assets/site.png" alt="" />
                    <h3>Portfolio Site</h3>
                </div>
                <div className="item">
                    <img src="assets/site.png" alt="" />
                    <h3>Portfolio Site</h3>
                </div>
            </div>
            
        </div>
    )
}
